const mailApi = require('forthright48/world').secretModule.mailApi;
const nodemailer = require('nodemailer');
const transport = require('nodemailer-mailgun-transport');
const domain = require('forthright48/world').secretModule.domain;

const mailer = nodemailer.createTransport(transport({
  auth : {
    api_key: mailApi,
    domain: `no-reply.${domain}`,
  }
}));

module.exports = {
  mailer,
};
